function select(ele,id){
	$jq("#"+id).text($jq(ele).text());
}

$(document).ready(function(){
	$('.slider').slider();
    $('[data-toggle="tooltip"]').tooltip(); 
	$('.tab_content').hide();
	$('.tab_content:first').show();
	$('.tabs li:first').addClass('active');
	$('.tabs li').click(function(event) {
		$('.tabs li').removeClass('active');
		$(this).addClass('active');
		$('.tab_content').hide();

		var selectTab = $(this).find('a').attr("href");

		$(selectTab).fadeIn();
	  });
	  
	  
	   $('#emailoffer').modal('show');
	   
	   
	   var manageTopSpacing = function(){
			$timeout(function() {
				$('.container-box:visible:first-child').css('margin-top', $('.sticky-panel:visible').height() + 85);
				window.scrollTo(0, 0);  
			});
		}
});
